import React,{useEffect, useState} from 'react'


const Homepage = () => {


  const [displayText, setDisplayText] = useState('');

  useEffect(() => {
    const text = "नमस्कार मंडळी"; // Text to be typed out
    let index = 0;
    const intervalId = setInterval(() => {
      if (index < text.length) {
        setDisplayText(prevText => prevText + text.charAt(index));
        index++;
      } else {
        clearInterval(intervalId);
      }
    }, 100); // Typing speed (adjust as needed)

    return () => clearInterval(intervalId); // Clean up interval on unmount
  }, []);

    return (
        <section className="bg-gray-900 text-white">
        <div class="mx-auto max-w-screen-xl px-4 py-12 lg:flex lg:h-screen lg:items-start">
          <div class="mx-auto max-w-3xl text-center">
            <h1
              class=" p-10 bg-gradient-to-r from-green-300 via-blue-500 to-purple-600 bg-clip-text text-3xl font-extrabold text-transparent sm:text-5xl"
            >
             {displayText}

              <span class=" m-5 sm:block"> Welcome to Hotel Midas </span>
            </h1>

            <p class="mx-auto mt-4 max-w-xl sm:text-xl/relaxed">
              Lorem ipsum dolor sit amet consectetur, adipisicing elit. Nesciunt illo tenetur fuga ducimus
              numquam ea!
            </p>

            <div class="mt-8 flex flex-wrap justify-center gap-4">
              <a
                class="block w-full rounded border border-blue-600 bg-blue-600 px-12 py-3 text-sm font-medium text-white hover:bg-transparent hover:text-white focus:outline-none focus:ring active:text-opacity-75 sm:w-auto"
                href="#"
              >
                Get Started
              </a>

              <a
                class="block w-full rounded border border-blue-600 px-12 py-3 text-sm font-medium text-white hover:bg-blue-600 focus:outline-none focus:ring active:bg-blue-500 sm:w-auto"
                href="#"
              >
                Learn More
              </a>
            </div>
          </div>
        </div>
      </section>
    );
}

export default Homepage
